INSERT INTO product_for_skintype (product_id, skintype_id)
SELECT p.id, s.id
FROM product p
         CROSS JOIN skintype s
WHERE
  -- For Dry skin type
    (p.description LIKE '%moisturizing%' OR
     p.description LIKE '%hydrating%' OR
     p.description LIKE '%nourishing%' OR
     p.description LIKE '%dry skin%') AND
        s.id = 1

UNION ALL

SELECT p.id, s.id
FROM product p
         CROSS JOIN skintype s
WHERE
  -- For Oily skin type
    (p.description LIKE '%oil-control%' OR
     p.description LIKE '%mattifying%' OR
     p.description LIKE '%oil-free%' OR
     p.description LIKE '%oily skin%') AND
        s.id = 2

UNION ALL

SELECT p.id, s.id
FROM product p
         CROSS JOIN skintype s
WHERE
  -- For Combination skin type
    (p.description LIKE '%combination skin%' OR
     p.description LIKE '%normal and oily%' OR
     p.description LIKE '%normal and dry%') AND
        s.id = 3

UNION ALL

SELECT p.id, s.id
FROM product p
         CROSS JOIN skintype s
WHERE
  -- For Sensitive skin type
    (p.description LIKE '%sensitive skin%' OR
     p.description LIKE '%gentle%' OR
     p.description LIKE '%for sensitive skin%') AND
        s.id = 4

UNION ALL

SELECT p.id, s.id
FROM product p
         CROSS JOIN skintype s
WHERE
  -- For Normal skin type
    (p.description LIKE '%normal skin%' OR
     p.description LIKE '%all skin types%' OR
     p.description Like '%any skin type%' ) AND
        s.id = 5;

