-- Populate the user_likes table
DELIMITER $$

CREATE PROCEDURE PopulateUserLikes()
BEGIN
    DECLARE user_count INT DEFAULT 1;
    DECLARE product_count INT;
    DECLARE like_count INT;

    -- Get the total number of products
    SELECT COUNT(*) INTO product_count FROM product;

    WHILE user_count <= 38 DO
            -- Insert likes for the current user
            INSERT INTO user_likes (user_id, product_id)
            SELECT user_count, id
            FROM product
            ORDER BY RAND()
            LIMIT 10;

            SET like_count = (SELECT COUNT(*) FROM user_likes WHERE user_id = user_count);

            -- Generate additional likes for each product until each product has been liked at least 5 times
            WHILE like_count < 10 DO
                    INSERT INTO user_likes (user_id, product_id)
                    SELECT user_count, id
                    FROM product
                    WHERE id NOT IN (SELECT product_id FROM user_likes WHERE user_id = user_count)
                    ORDER BY RAND()
                    LIMIT 1;

                    SET like_count = like_count + 1;
                END WHILE;

            SET user_count = user_count + 1;
        END WHILE;

END$$

DELIMITER ;

-- Call the stored procedure to populate user_likes
CALL PopulateUserLikes();


-- update the product table to have the correct number of likes according to user_likes table
UPDATE product p
SET likes = (SELECT COUNT(*) FROM user_likes ul WHERE ul.product_id = p.id);




